import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../Styles/ProductDetails.css';

const ProductCard = ({ name, price, image, description, addToFavorites, addToCart }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('description');

  const product = { id, name, price, image, description }; // –ò—Å–ø–æ–ª—å–∑—É–µ–º –ø–µ—Ä–µ–¥–∞–Ω–Ω—ã–µ props

  if (!product) {
    return <div>Product not found</div>;
  }

  const handleBack = () => {
    navigate('/gallery');
  };

  return (
    <div className="product-page">
      <div className="product-card-details">
        <div className="product-image-section">
          <img src={product.image} alt={product.name} />
        </div>
        <div className="product-info-section">
          <h2>{product.name}</h2>
          <p>{product.price}</p>

          <div className="tabs">
            <button className={activeTab === 'description' ? 'active' : ''} onClick={() => setActiveTab('description')}>–û–ø–∏—Å–∞–Ω–∏–µ</button>
            <button className={activeTab === 'composition' ? 'active' : ''} onClick={() => setActiveTab('composition')}>–°–æ—Å—Ç–∞–≤</button>
            <button className={activeTab === 'application' ? 'active' : ''} onClick={() => setActiveTab('application')}>–ü—Ä–∏–º–µ–Ω–µ–Ω–∏–µ</button>
          </div>

          {activeTab === 'description' && <p>{product.description}</p>}
          {activeTab === 'composition' && <p>{product.composition}</p>}
          {activeTab === 'application' && <p>{product.application}</p>}

          <div className="product-card-buttons">
            <button className="like-button" onClick={() => addToFavorites(product)}>‚ù§ –ü–æ–Ω—Ä–∞–≤–∏–ª–æ—Å—å</button>
            <button className="cart-button" onClick={() => addToCart(product)}>üõí –í –∫–æ—Ä–∑–∏–Ω—É</button>
            <button className="back-button" onClick={handleBack}>‚Üê –ù–∞–∑–∞–¥ –∫ –∫–∞—Ç–∞–ª–æ–≥—É</button>
          </div>
        </div>
      </div>
    </div>
  );
};

