import React, { useState } from 'react';
import './App.css';

const NavBar = ({ setActivePage }) => (
  <div class="topnav">
  <ul>
    <li><a href="#" onClick={() => setActivePage('сheesecake')}>Cheesecake</a></li>
    <li><a href="#" onClick={() => setActivePage('order')}>Order Online</a></li>
    <li><a href="#" onClick={() => setActivePage('about')}>About Us</a></li>
    <li><a href="#" onClick={() => setActivePage('contacts')}>Contacts</a></li>
  </ul>
  </div>
);

const Content = ({ activePage }) => {
  let content;
  switch (activePage) {
    case 'cheesecake':
      content = (
        <div className="content fade-in"> 
          <div className="img-container">
            <img src="https://static.vecteezy.com/system/resources/thumbnails/025/066/829/small/cheesecake-with-ai-generated-free-png.png" alt="Cheesecake" className="fade-in" /> {/* Добавляем класс fade-in */}
          </div>
          <p>Learn about our delicious cheesecake!</p>
        </div>
      );
      break;
    case 'order':
      content = (
        <div className="content fade-in">
          <div className="img-container">
            <img src="https://static.vecteezy.com/system/resources/thumbnails/025/066/829/small/cheesecake-with-ai-generated-free-png.png" alt="Order Online" className="fade-in" /> {/* Добавляем класс fade-in */}
          </div>
          <p>Order your favorite cheesecake online!</p>
        </div>
      );
      break;
    case 'about':
      content = (
        <div className="content fade-in">
          <div className="img-container">
            <img src="https://static.vecteezy.com/system/resources/thumbnails/025/066/829/small/cheesecake-with-ai-generated-free-png.png" alt="About Us" className="fade-in" /> {/* Добавляем класс fade-in */}
          </div>
          <p>Learn about our company and mission!</p>
        </div>
      );
      break;
    case 'contacts':
      content = (
        <div className="content fade-in">
          <div className="img-container">
            <img src="https://static.vecteezy.com/system/resources/thumbnails/025/066/829/small/cheesecake-with-ai-generated-free-png.png" alt="Contacts" className="fade-in" /> {/* Добавляем класс fade-in */}
          </div>
          <p>Contact us for any inquiries!</p>
        </div>
      );
      break;
    default:
      content = <div>Page not found</div>;
  }
  return content;
};

const App = () => {
  const [activePage, setActivePage] = useState('сheesecake');

  return (
    <div>
      <NavBar setActivePage={setActivePage} />
      <Content activePage={activePage} />
    </div>
  );
};

export default App;
