const App = () => {
  const [favorites, setFavorites] = useState([]);
  const [cart, setCart] = useState([]);
  const [topProducts, setTopProducts] = useState([]);

  const addToCart = (product) => {
    setCart([...cart, product]);
  };

  const addToFavorites = (product) => {
    setFavorites([...favorites, product]);
  };

  return (
    <Router>
      <div className="app">
        <Header />
        <main>
          <Routes>
            <Route path="/" element={<HomePage topProducts={topProducts} />} />
            <Route path="/about" element={<AboutPage />} />
            <Route path="/gallery" element={<GalleryPage addToCart={addToCart} addToFavorites={addToFavorites} sendTopProducts={setTopProducts} />} />
            <Route path="/product/:id" element={<ProductCard addToCart={addToCart} addToFavorites={addToFavorites} />} />
            <Route path="/contact" element={<ContactPage />} />
            <Route path="/login" element={<LoginWrapper />} />
            <Route path="/register" element={<RegisterForm />} />
            <Route path="/favorites" element={<FavoritesPage favorites={favorites} />} />
            <Route path="/cart" element={<CartPage cart={cart} />} />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
};

export default App;
