import React, { useState } from 'react';
import './App.css';

const NavBar = ({ setActivePage }) => (
  <ul>
    <li onClick={() => setActivePage('сheesecake')}>Cheesecake</li>
    <li onClick={() => setActivePage('order')}>Order Online</li>
    <li onClick={() => setActivePage('about')}>About Us</li>
    <li onClick={() => setActivePage('contacts')}>Contacts</li>
  </ul>
);

const Content = ({ activePage }) => {
  const [animation, setAnimation] = useState('slide-in');

  const onAnimationEnd = () => {
    if (animation === 'slide-out') {
      setAnimation('slide-in');
    }
  };

  let content;
  switch (activePage) {
    case 'coffee':
      content = (
        <div className={`content ${animation}`} onAnimationEnd={onAnimationEnd}>
          <div className="img-container">
            <img src="https://static.vecteezy.com/system/resources/thumbnails/025/066/829/small/cheesecake-with-ai-generated-free-png.png" alt="Coffee" />
          </div>
          <p>Learn about our delicious coffee!</p>
        </div>
      );
      break;
    case 'order':
      content = (
        <div className={`content ${animation}`} onAnimationEnd={onAnimationEnd}>
          <div className="img-container">
            <img src="https://static.vecteezy.com/system/resources/thumbnails/025/066/829/small/cheesecake-with-ai-generated-free-png.png" alt="Order Online" />
          </div>
          <p>Order your favorite coffee online!</p>
        </div>
      );
      break;
    case 'about':
      content = (
        <div className={`content ${animation}`} onAnimationEnd={onAnimationEnd}>
          <div className="img-container">
            <img src="https://static.vecteezy.com/system/resources/thumbnails/025/066/829/small/cheesecake-with-ai-generated-free-png.png" alt="About Us" />
          </div>
          <p>Learn about our company and mission!</p>
        </div>
      );
      break;
    case 'contacts':
      content = (
        <div className={`content ${animation}`} onAnimationEnd={onAnimationEnd}>
          <div className="img-container">
            <img src="https://static.vecteezy.com/system/resources/thumbnails/025/066/829/small/cheesecake-with-ai-generated-free-png.png" alt="Contacts" />
          </div>
          <p>Contact us for any inquiries!</p>
        </div>
      );
      break;
    default:
      content = <div>Page not found</div>;
  }
  return content;
};

const App = () => {
  const [activePage, setActivePage] = useState('coffee');

  return (
    <div>
      <NavBar setActivePage={setActivePage} />
      <Content activePage={activePage} />
    </div>
  );
};

export default App;
