import React from 'react';
import { Link } from 'react-router-dom';
import '../Styles/CartPage.css';

const CartPage = ({ cart }) => {
  const totalSum = cart.reduce((sum, item) => sum + parseFloat(item.price.replace('₸', '').replace(' ', '')), 0);

  return (
    <div className="cart-page">
      <h2>Корзина</h2>
      <div className="cart-list">
        {cart.map((item) => (
          <div key={item.id} className="cart-item">
            <img src={item.image} alt={item.name} />
            <p>{item.name} - {item.price}</p>
          </div>
        ))}
      </div>
      <div className="order-summary">
        <h3>Сумма заказа</h3>
        <p>Стоимость продуктов: <span>{totalSum.toLocaleString()} ₸</span></p>
        <p>Итого: <span>{totalSum.toLocaleString()} ₸</span></p>
        <button className="order-button">Оформить заказ</button>
      </div>
    </div>
  );
};

export default CartPage;
