            <img src={product.image} alt={product.name} className="product-image" />
            <h3>{product.name}</h3>
            <p>{product.price}</p>
            <div className="product-card-buttons">
              <Link to={'/product/${product.id}'} className="view-details">–ü–æ–¥—Ä–æ–±–Ω–µ–µ</Link>
