import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Slider from 'react-slick';
import '../Styles/HomePage.css';
import ProductCard from '../Productcard/ProductCard';
import products from '../Productcard/products';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const HomePage = () => {
  const [reviews, setReviews] = useState([
    { name: 'Alice', comment: 'Great products!' },
    { name: 'Bob', comment: 'Excellent quality and fast shipping.' }
  ]);
  const [newReview, setNewReview] = useState({
    name: '',
    comment: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewReview({ ...newReview, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setReviews([...reviews, newReview]);
    setNewReview({ name: '', comment: '' });
  };

  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true, 
    autoplaySpeed: 3000,
  };

  return (
    <div className="homepage">
      <div className="welcome-page">
      <h1>Добро пожаловать!</h1>
      <p>Ваш магазин для всего, что вам нужно для красоты!</p>
      <br></br>
      <Link to="/gallery" className="shop-now">Купить сейчас</Link>
      </div>

      <div className="slider-section">
        <h2>Новинки</h2>
        <Slider {...sliderSettings}>
          <div className="slide">
            <img src="https://www.monamie.kz/upload/resize_cache/iblock/8d8/1322_469_19eee6f261393ada4ccf6aacee469d351/8d8be8fc3bb83cc8231b4853d0badf31.jpg" alt="Slide 1" />
          </div>
          <div className="slide">
            <img src="https://www.monamie.kz/upload/resize_cache/iblock/3c2/1322_469_19eee6f261393ada4ccf6aacee469d351/3c24b632f7e87cbf68ee1608b0e19a69.jpg" alt="Slide 2" />
          </div>
          <div className="slide">
            <img src="https://www.monamie.kz/upload/resize_cache/iblock/31f/1322_469_19eee6f261393ada4ccf6aacee469d351/31fce6f1dc5f7c810808d18669b7bbdd.jpg" alt="Slide 3" />
          </div>
        </Slider>
      </div>

      <div className="products-section">
        <h2>Our Products</h2>
        <div className="products-list">
          {products.map((product) => (
            <Link key={product.id} to={`/product/${product.id}`}>
              <ProductCard 
                name={product.name}
                price={product.price}
                description={product.description}
                image={product.image}
              />
            </Link>
          ))}
        </div>
      </div>

      <div className="reviews-section">
        <h2>Customer Reviews</h2>
        <div className="reviews-list">
          {reviews.map((review, index) => (
            <div key={index} className="review">
              <h3>{review.name}</h3>
              <p>{review.comment}</p>
            </div>
          ))}
        </div>
        {user ? (
          <form onSubmit={handleSubmit}>
            <h3>Leave a Review</h3>
            <div>
              <label htmlFor="name">Name:</label>
              <input
                type="text"
                id="name"
                name="name"
                value={newReview.name}
                onChange={handleChange}
                required
              />
            </div>
            <div>
              <label htmlFor="comment">Comment:</label>
              <textarea
                id="comment"
                name="comment"
                value={newReview.comment}
                onChange={handleChange}
                required
              />
            </div>
            <button type="submit">Submit Review</button>
          </form>
        ) : (
          <div>
            <p>Please log in to leave a review.</p>
            <LoginForm onLogin={handleLogin} />
          </div>
        )}
      </div>
    </div>
  );
};

export default HomePage;
