import React from 'react';
import { useParams } from 'react-router-dom';

const profilesData = [
  { id: 1, name: 'Alice', age: 25, about: 'Engineering is the practice of using natural science, mathematics, and the engineering design process[1] to solve technical problems, increase efficiency and productivity, and improve systems. Modern engineering comprises many subfields which include designing and improving infrastructure, machinery, vehicles, electronics, materials, and energy systems.Engineer' },
  { id: 2, name: 'Bob', age: 30, about: 'Engineering is the practice of using natural science, mathematics, and the engineering design process[1] to solve technical problems, increase efficiency and productivity, and improve systems. Modern engineering comprises many subfields which include designing and improving infrastructure, machinery, vehicles, electronics, materials, and energy systems.' },
  { id: 3, name: 'Charlie', age: 35, about: 'Developer. jhcsdhcsjdvjdhvdhdvojovf' },
  { id: 4, name: 'David', age: 40, about: 'Artist. hcgysdgciducdjcdlcjdl' },
  { id: 5, name: 'Eve', age: 45, about: 'Musician. Engineering is the practice of using natural science, mathematics, and the engineering design process[1] to solve technical problems, increase efficiency and productivity, and improve systems. Modern engineering comprises many subfields which include designing and improving infrastructure, machinery, vehicles, electronics, materials, and energy systems.' },
];

function ProfileDetail() {
  const { id } = useParams();
  const profile = profilesData.find(profile => profile.id === parseInt(id));

  if (!profile) return <div>Profile not found</div>;

  return (
    <div>
      <h1>Profile Detail</h1>
      <p>Name: {profile.name}</p>
      <p>Age: {profile.age}</p>
      <p>About: {profile.about}</p>
    </div>
  );
}

export default ProfileDetail;
