import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const HomePage = () => {
  const [products] = useState([
    { id: 1, name: 'Lipstick', price: '$15', description: 'A beautiful lipstick.', image: '/images/lipstick.jpg' },
    { id: 2, name: 'Mascara', price: '$20', description: 'A voluminous mascara.', image: '/images/mascara.jpg' },
    { id: 3, name: 'Foundation', price: '$25', description: 'A smooth foundation.', image: '/images/foundation.jpg' },
  ]);
  
  const [reviews, setReviews] = useState([
    { name: 'Alice', comment: 'Great products!' },
    { name: 'Bob', comment: 'Excellent quality and fast shipping.' }
  ]);
  const [newReview, setNewReview] = useState({
    name: '',
    comment: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewReview({ ...newReview, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setReviews([...reviews, newReview]);
    setNewReview({ name: '', comment: '' });
  };

  return (
    <div className="homepage">
      <h1>Welcome to Beauty Store</h1>
      <p>Your one-stop shop for all your beauty needs!</p>
      <Link to="/gallery" className="shop-now">Shop Now</Link>

      <div className="products-section">
        <h2>Our Products</h2>
        <div className="products-list">
          {products.map((product) => (
            <div key={product.id} className="product">
              <img src={product.image} alt={product.name} />
              <h3>{product.name}</h3>
              <p>{product.price}</p>
              <p>{product.description}</p>
            </div>
          ))}
        </div>
      </div>

      <div className="reviews-section">
        <h2>Customer Reviews</h2>
        <div className="reviews-list">
          {reviews.map((review, index) => (
            <div key={index} className="review">
              <h3>{review.name}</h3>
              <p>{review.comment}</p>
            </div>
          ))}
        </div>
        <form onSubmit={handleSubmit}>
          <h3>Leave a Review</h3>
          <div>
            <label htmlFor="name">Name:</label>
            <input
              type="text"
              id="name"
              name="name"
              value={newReview.name}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label htmlFor="comment">Comment:</label>
            <textarea
              id="comment"
              name="comment"
              value={newReview.comment}
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit">Submit Review</button>
        </form>
      </div>
    </div>
  );
};

export default HomePage;
